import React, { Component } from 'react';
import axios from "axios"
import { Button } from 'react-bootstrap';

class ShowUsers extends Component {
    state = {
        users: []
      }
    getUsers = () => {
        axios.get("/getUsersInfo").then(response => {
            this.prepareData(response.data)
        })
    }

    prepareData = (ee) => {
        var elo = []
        for(var e of ee){
            elo.push([e.username,e.ratings])
        }
        console.log(elo)
        this.setState({users:elo})
    }

    printResults = () => {
        if(this.state.users.length == 0){
            return ""
        }
    return this.state.users.map(e => {return <div><h1>{e[0]}</h1> {e[1].map(f => <React.Fragment><p>title: {f[1]}</p><p>rating: {f[0]}</p></React.Fragment> )} </div>})
    }

    render() { 
        return <React.Fragment>
            <Button onClick={() => this.getUsers()}>
            JAZDA
            </Button>
            {this.printResults()}
        </React.Fragment>
    }
}

export default ShowUsers;
