import React, { Component } from 'react';
import { Button,InputGroup,FormControl } from 'react-bootstrap';
import ReactStars from "react-rating-stars-component";
import axios from "axios"
class RateMovie extends Component {
    state = { 
        movies: ["The Shawshank Redemption 1994","The Green Mile 1999","Requiem for a Dream 2000","The Matrix 1999"],
        username:"",
        ratings: []
      }
      ratingChanged = (newRating,title) => {
            var elo = this.state.ratings;
            elo.push([newRating,title])
            this.setState({ratings:elo});
      };

      usernameChanged = (name) => {
          this.setState({username:name})
      }
    makeRequest = () => {
        axios.post("/saveUser",{ratings:this.state.ratings,username:this.state.username})
    }

    listElement = (element) => {
        return <div>
            <h1>{element}</h1>
            <ReactStars
                count={5}
                onChange={ e => this.ratingChanged(e,element)}
                size={24}
                activeColor="#ffd700"
            />
        </div>
    }
    render() { 
        return <div>
            <div className="jaz">
                {this.state.movies.map(e => <h1>{this.listElement(e)}</h1> )}
            </div>
            <div>
                <InputGroup className="mb-3">
                    <FormControl
                    onChange={this.usernameChanged}
                    placeholder="Username"
                    aria-label="Username"
                    aria-describedby="basic-addon1"
                    />
                </InputGroup>
                <Button onClick={this.makeRequest}>Submit</Button>
            </div>
        </div>

    }

}

export default RateMovie;
