import React, { Component } from 'react';
import { Button } from 'react-bootstrap';
import ReactStars from "react-rating-stars-component";

class RateMovie extends Component {
    state = { 
        movies: ["The Shawshank Redemption 1994","The Green Mile 1999","Requiem for a Dream 2000","The Matrix 1999"]
      }
      ratingChanged = (newRating,title) => {
        console.log(newRating);
        console.log(title);
      };

    listElement = (element) => {
        return <div>
            <h1>{element}</h1>
            <ReactStars
                count={5}
                onChange={ e => this.ratingChanged(e,element)}
                size={24}
                activeColor="#ffd700"
            />
        </div>
    }
    render() { 
        return <React.element>
            <div className="jaz">
            {this.state.movies.map(e => <h1>{this.listElement(e)}</h1> )}
        </div>
        <div>
        <InputGroup className="mb-3">
    <InputGroup.Prepend>
      <InputGroup.Text id="basic-addon1">@</InputGroup.Text>
    </InputGroup.Prepend>
    <FormControl
      placeholder="Username"
      aria-label="Username"
      aria-describedby="basic-addon1"
    />
  </InputGroup>
  <Button>Submit</Button>
        </div>
        </React.element>;

    }

}

export default RateMovie;
